class Prices extends React.Component {
    state = {
        currency : 'USD'
    }

    render() {
        let list = '';
        if (this.state.currency === 'USD') {
            list = <li className="list-group-item">
                Bitcoin rate for {this.props.bpi.bpi.USD.description} :
                        <span className="badge badge-primary">
                    {this.props.bpi.bpi.USD.code}
                </span>
                <strong>{this.props.bpi.bpi.USD.rate}</strong>
            </li>
        } else if (this.state.currency === 'GBP') {
            <li className="list-group-item">
                Bitcoin rate for {this.props.bpi.bpi.USD.description} :
                        <span className="badge badge-primary">
                    {this.props.bpi.bpi.GBP.code}
                </span>
                <strong>{this.props.bpi.bpi.USD.rate}</strong>
            </li>
        } else if (this.state.currecy === 'ERU') {
            <li className="list-group-item">
                Bitcoin rate for {this.props.bpi.bpi.ER.description} :
                        <span className="badge badge-primary">
                    {this.props.bpi.bpi.ERU.code}
                </span>
                <strong>{this.props.bpi.bpi.ERU.rate}</strong>
            </li>
        }
        return (
            <div>
                <ul className="list-group">
                    <li className="list-group-item">
                        Bitcoin rate for {this.props.bpi.bpi.USD.description} :
                        <span className="badge badge-primary">
                            {this.props.bpi.bpi.USD.code}
                        </span>
                        <strong>{this.props.bpi.bpi.USD.rate}</strong>
                    </li>
                </ul>
            </div>
        )
    }
}

export default Prices
